import { createContext, useState, useContext } from 'react';
import axios from 'axios';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [token, setToken] = useState(null);

    const login = async (email, password) => {
        try {
            const res = await axios.post('http://localhost:3002/login', { email, password });
            setUser(res.data.user);
            setToken(res.data.token);
            return true;
        } catch (error) {
            console.error('Login error:', error);
            return false;
        }
    };

    const updateScore = async (score) => {
        if (!token) {
            console.error('No token found');
            return false;
        }
        try {
            await axios.post('http://localhost:3002/updateScore', { token, score });
            setUser((prevUser) => ({ ...prevUser, score }));
            return true;
        } catch (error) {
            console.error('Update score error:', error);
            return false;
        }
    };

    return (
        <AuthContext.Provider value={{ user, token, login, updateScore }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => useContext(AuthContext);
